                      data-testid={`button-${action.title.toLowerCase().replace(' ', '-')}`}
                      onClick={() => {
                        const formElement = document.getElementById('contact-form-section');
                        if (formElement) {
                          formElement.scrollIntoView({ behavior: 'smooth' });
